app.controller('AccessoriesController',['$scope','Accessories',function($scope,Accessories){
    Accessories.async().then(function(d) {
        $scope.items = d;
        console.log($scope.items)
    });

}]);
app.filter('chooseCharacter', function () {
    return function (input, str) {
        var filtered = [];
        angular.forEach(input, function(item) {
            if(item.character.toLowerCase() == str || str == undefined) {
                filtered.push(item);
            }
        });
        return filtered;

    };
});
var AccessoriesService = angular.module('AccessoriesService', [])
    .service('Accessories',['$http' ,function ($http) {
        var accessoriesService = {
            async: function() {
                // $http returns a promise, which has a then function, which also returns a promise
                var promise = $http.get('getdata.php?group=accessories').then(function (response) {
                    // The then function here is an opportunity to modify the response
                    //console.log(response);
                    // The return value gets picked up by the then in the controller.
                    return response.data;
                });
                // Return the promise to the controller
                return promise;
            }
        };
        return accessoriesService;
    }]);
